import React from "react"
import Title from "./Title";
import Icons from "../Components/Icons"
import { useState } from "react"

export default function Board() {
    const [player, setPlayer] = useState("player1");

    function turn() {
        if (player === "player1") {
            setPlayer("player2");

        } else if (player === "player2") {
            setPlayer("player1");

        }
    }



    return (
        <>
            <Title player={player} />
            <div class="board">
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <div class="element" onClick={turn}>
                    <Icons turn={player} />
                </div>
                <style>
                    {`
            .board{
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            grid-template-rows: repeat(3, 1fr); 
            }
            .element{
            width: auto;
            height: 100%;
            cursor: pointer;
            border: 1px solid black;
            margin-top: 100px;
            }
            `}
                </style>
            </div>
        </>
    )
}
