import React from "react"
import Title from "./Title";
import Icons from "../Components/Icons"
import { useState } from "react"

export default function Board() {
    const [player, setPlayer] = useState("player1");

    function turn() {
        if (player === "player1") {
            setPlayer("player2");
            icono();
        } else if (player === "player2") {
            setPlayer("player1");
            icono();
        }
    }

    function icono() {
        if (player === "player1") {
            return <div><Icons turn="player1" />;    </div>
        } else if (player === "player2") {
           return <Icons turn="player2" />;
        }
    };

    return (
        <>
            <Title player={player} />
            <div class="board">
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <div class="element" onClick={turn}></div>
                <style>
                    {`
            .board{
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            grid-template-rows: repeat(3, 1fr); 
            }
            .element{
            width: auto;
            height: 100%;
            cursor: pointer;
            border: 1px solid black;
            margin-top: 100px;
            }
            `}
                </style>
            </div>
        </>
    )
}
