import React from "react"

export default function Box(props) {
    const [on, setOn] = React.useState(props.on)

    // const styles = {
    //     backgroundColor: on ? "#222222" : "transparent",
    //     height: "100px",
    //     width: "100px",
    //     border: "1px solid black",
    //     display: "inline-block",
    //     marginRight: "4px",
    //     borderRadius: "5px"
    // }

    function toggle() {
        setOn(prevOn => !prevOn)
    }

    return (
        <>
            <div className="box" onClick={toggle}></div>
            <style >{`
            .box{
                background-color: ${on ? "#222222" : "transparent"},
                height: 100px;
            width: 100px;
            border: 1px solid black;
            display: inline-block;
            margin-right: 4px;
            border-radius: 5px;
        }
            `}

            </style>
        </>
    )
}
