import React from "react"

export default function Title(props) {

    function title() {
        if (props.player == "player1") {
            return "1";
        } else {
            return "2";
        }
    };

    const text = "Turno del jugador " + title();

    return (
        <div className="board">
            <h1> TA TE TI</h1>
            <h5 className="player-turn">{text}</h5>
            <style>{`
            .board {
                display: flex;
                flex-wrap: wrap;
                justify-content: center;
                align-items: center;

              }
              h1{
                background-color: #bb86fc;
                width: 100%:
                text-align: center;
              }
             .player-turn{
                margin-top: 16px;
                text-align: center;
            }
                `}
            </style>
        </div>
    )
}
