import React from "react"
import Title from "./Title";
import Icons from "../Components/Icons"
import {useState} from "react"

state = {
    player : "player1"
}

export default function Board() {


    function turn() {
        if (player === "player1") {
            this.setState({player:  "player2"});
            icono();
            console.log(player);
        } else if (player === "player2") {
            this.setState({player:  "player1"});
            icono();
            console.log(player);
        }
    }

    function icono() {
        if (player === "player1") {
            <Icons turn="player1" />
            console.log("icon1");
        } else if (player.player === "player2") {
            <Icons turn="player2" />
            console.log("icon");
        }
    };

    return (
        <>
        <Title player={player} />
        <div class="board">
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <style>
                {`
            .board{
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            grid-template-rows: repeat(3, 1fr); 
            }
            .element{
            width: auto;
            height: 100%;
            cursor: pointer;
            border: 1px solid black;
            margin-top: 100px;
            }
            `}
            </style>
        </div>
        </>
    )
}
