import React from "react"
import Icons from "../Components/Icons"

export default function Board() {

    let player = "player1";

    function turn() {
        if (icono && (player === "player1")) {
            player = "player2";
            console.log("turno2");
        } else if (!icono && (player === "player2")) {
            player = "player1";
            console.log("turno1");
        }
    }

    function icono() {
        if (player === "player1") {
            <Icons turn="player1" />
            console.log("icon");
            return true;
        } else if (player.player === "player2") {
            <Icons turn="player2" />
            console.log("icon");
            return false;
        }
    };

    return (
        <div class="board">
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <div class="element" onClick={turn}></div>
            <style>
                {`
            .board{
            display: grid;
            grid-template-columns: repeat(3, 1fr);
            grid-template-rows: repeat(3, 1fr); 
            }
            .element{
            width: auto;
            height: 100%;
            cursor: pointer;
            border: 1px solid black;
            margin-top: 100px;
            }
            `}
            </style>
        </div>
    )
}
